import React from "react";
import Park from "./Park";


function createParkComponents(parkComponents, parkComponentsList, Allparks) {
    for (let i = 0; i < Allparks.length; i++) { 
        parkComponents[Allparks[i].fullName] = Park(Allparks[i]);
        parkComponentsList.append(Allparks[i]);
    }
    return parkComponents;
}

export default class ParkList extends React.Component {
    state = {
        visible: true,
        loading: true,
        allParks: [],
        parkComponentsList: {},
        parkComponentsDict: {},
    };

    async componentDidMount() {
        const url = "https://developer.nps.gov/api/v1/parks?limit=1000&api_key=gUeSemHr93mXuKE5WqakVIaO2cDN67aqXettWyDE";
        const response = await fetch(url);
        const data = await response.json();
        this.setState({ Allparks: data.data, loading: false });
        createParkComponents(this.state.parkComponents, this.parkComponentsList, this.state.allParks);
    }

    render() {
        return (
            <div>
              {this.state.Allparks.map((el, idx) => (
                <>
                  {this.state.visible ? <Park 
                    fullName={ el.fullName } 
                    description={ el.description }
                    addresses={ JSON.stringify(el.addresses) }
                  /> : null}
                </>
              ))}
            </div>
          );

   }
}
