import { useParams } from "react-router";
import useFetch from "./useFetch";

const ParkDetails = () => {
    const { id } = useParams()
    const { data: park, error, isPending } = useFetch('https://developer.nps.gov/api/v1/parks?parkCode=' + id + '&limit=10&api_key=gUeSemHr93mXuKE5WqakVIaO2cDN67aqXettWyDE');
    return (
        <div className="park-details">
            { isPending && <div>Loading...</div>}
            { error && <div>{ error }</div>}
            { park && (
                <article> 
                    <h2> { park.fullName } </h2>
                    <div> { park.addresses[0].line1} , { park.addresses[0].city} , { park.addresses[0].stateCode} , { park.addresses[0].postalCode}</div>
                    <div> { park.description} </div>
                    <img> </img>
                </article>

            )}
        </div>
    );
}

export default ParkDetails;
