import { useEffect, useState } from 'react';
import ActivityList from './ActivityList';
import useFetch from './useFetch';
import { Link } from 'react-router-dom';


const Activities = () => {
    //const { data: Alldata, isPending, error } = useFetch("https://developer.nps.gov/api/v1/activities/parks?&limit=1000&api_key=gUeSemHr93mXuKE5WqakVIaO2cDN67aqXettWyDE");
    //console.log(Alldata);
    //console.log(parks);
    //const parks = data;
    //const [Activities, setActivities] = useState([]);

    /*const handleActivitySearch = (activity) => {
        const newParks = parks.filter(park => park.activities !== activity);
        setParks(newParks);
    }*/

    const [data, setData] = useState(null);
    const [isPending, setIsPending] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        setTimeout(() => {
          fetch("https://developer.nps.gov/api/v1/activities/parks?&limit=1000&api_key=gUeSemHr93mXuKE5WqakVIaO2cDN67aqXettWyDE")
          .then(res => {
            return res.json();
          })
          .then(data => {
            setIsPending(false);
            setData(data.data);
          })
        }, 1000);
      }, [])

    console.log(data);


    return (  
        <div className="home">
        <h2>Activities</h2>
            { error && <div>{ error }</div> }
            { isPending && <div>Loading...</div> }
            {data && (data.map(function(activityEle, idx){
                <Link to={`/Activities/${activityEle.id}`}> <li key={idx}>{activityEle.id}</li>
                <ActivityList activityId={activityEle.id} />
                </Link>
            }))}
        </div>
    );
}

export default Activities;
