import { useParams } from "react-router-dom";
import useFetch from "./useFetch";

const ParkDetails = () => {
    const { parkCode } = useParams();
    console.log(parkCode);
    const { data: park, error, isPending } = useFetch(`https://developer.nps.gov/api/v1/parks?parkCode=${parkCode}&api_key=gUeSemHr93mXuKE5WqakVIaO2cDN67aqXettWyDE`);
    console.log(park);
    let parkData = park.data[0];
    return (
        <div className="park-details">
            { isPending && <div>Loading...</div>}
            { error && <div>{ error }</div>}
            { parkData && (
                <article> 
                    <h3>{ parkData.fullName } </h3>
                    <p> { parkData.addresses[0].line1 } { parkData.addresses[0].city } { parkData.addresses[0].stateCode } , { parkData.addresses[0].postalCode }</p>
                    <div> { parkData.description } </div>
                </article>
            )}

        </div>
    );
}

export default ParkDetails;
