import { useEffect, useState } from 'react';
import AllParkList from './AllParkList';

const Home = () => {
    // let name = 'mario';
    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [name, setName] = useState ('mario');
    const [parks, setParks] = useState([]);
    const [Activities, setActivities] = useState([]);

    //const [activity, setActivities]

    const handleActivitySearch = (activity) => {
        const newParks = parks.filter(park => park.activities !== activity);
        setParks(newParks);
    }

    useEffect(() => {
        fetch("https://developer.nps.gov/api/v1/parks?limit=1000&api_key=gUeSemHr93mXuKE5WqakVIaO2cDN67aqXettWyDE")
        .then(res => res.json())
        .then(
            (result) => {
            setIsLoaded(true);
            setParks(result.data);
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
            setIsLoaded(true);
            setError(error);
            }
        )
        fetch("https://developer.nps.gov/api/v1/webcams?limit=10000&api_key=gUeSemHr93mXuKE5WqakVIaO2cDN67aqXettWyDE")
        .then(res => res.json())
        .then(
            (result) => {
            setIsLoaded(true);
            setActivities(result.data);
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
            setIsLoaded(true);
            setError(error);
            }
        )
    }, [])

    const handleClick = (e) => {
        setName('luigi');
        console.log('hello, world', e);
    }

    const handleClickAgain = (name, e) => {
        console.log('hello ' + name, e.target);
    }

    return (  
        <div className="home">
            <AllParkList parks={parks} title="All Parks"/>
            <AllParkList parks={parks.filter((park) => park.fullName === "Abraham Lincoln Birthplace National Historical Park")} title="Select Parks"/>
        </div>
    );
}

export default Home;
