import { useEffect, useState } from 'react';
import ActivityList from './ActivityList';
import useFetch from './useFetch';
import { Link } from 'react-router-dom';


const Activities = () => {
    //const { data: Alldata, isPending, error } = useFetch("https://developer.nps.gov/api/v1/activities/parks?&limit=1000&api_key=gUeSemHr93mXuKE5WqakVIaO2cDN67aqXettWyDE");
    //console.log(Alldata);
    //console.log(parks);
    //const parks = data;
    //const [Activities, setActivities] = useState([]);

    /*const handleActivitySearch = (activity) => {
        const newParks = parks.filter(park => park.activities !== activity);
        setParks(newParks);
    }*/

    const [data, setData] = useState(null);
    const [isPending, setIsPending] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() =>  {
        const abortCont = new AbortController();

        setTimeout(() => {
            fetch("https://developer.nps.gov/api/v1/activities/parks?&limit=1000&api_key=gUeSemHr93mXuKE5WqakVIaO2cDN67aqXettWyDE", { signal: abortCont.signal })
                .then(res => {
                    if(!res.ok) {
                        throw Error('could not fetch data for given resource');
                    }
                    return res.json();
                })
                .then(data => {
                    setData(data.data);
                    setIsPending(false);
                    setError(null);
                })
                .catch(err => {
                    if (err.name === 'AbortError') {
                        console.log('fetch aborted');
                    }
                    setIsPending(false);
                    setError(err.message);
                })
                // Note: it's important to handle errors here
                // instead of a catch() block so that we don't swallow
                // exceptions from actual bugs in components.
        }, 1000);

        //return () => abortCont.abort();
    }, );

    console.log(data);


    return (  
        <div className="home">
        <h2>Activities</h2>
            { error && <div>{ error }</div> }
            { isPending && <div>Loading...</div> }
            {data && (data.map(function(activityEle, idx){
                <Link to={`/Activities/${activityEle.id}`}> <li key={idx}>{activityEle.id}</li>
                <ActivityList activityId={activityEle.id} />
                </Link>
            }))}
        </div>
    );
}

export default Activities;
