import { useEffect, useState } from 'react';
import AllParkList from './AllParkList';
import useFetch from './useFetch';

const Home = () => {
    const {parks: allParks, isPending, Error } = useFetch("https://developer.nps.gov/api/v1/parks?limit=1000&api_key=gUeSemHr93mXuKE5WqakVIaO2cDN67aqXettWyDE");

    const [name, setName] = useState ('mario');
    //const [parks, setParks] = useState([]);
    const [Activities, setActivities] = useState([]);

    const [namenew, setNamenew] = useState('mario');

    const handleActivitySearch = (activity) => {
        const newParks = parks.filter(park => park.activities !== activity);
        setParks(newParks);
    }

    const handleClick = (e) => {
        setName('luigi');
        console.log('hello, world', e);
    }

    const handleClickAgain = (name, e) => {
        console.log('hello ' + name, e.target);
    }

    return (  
        <div className="home">
            <button onClick={() => setName('luigi')}>change name</button>
            {parks && <AllParkList parks={parks} title="All Parks"/>}
            <AllParkList parks={parks.filter((park) => park.fullName === "Abraham Lincoln Birthplace National Historical Park")} title="Select Parks"/>
        </div>
    );
}

export default Home;
